mapper.select:
/*管理员查看询价表*/ select ppi.id,ppi.price_inqury_id,pcu.company_name,pcu.contect_name,su.mobile, ppi.create_time,ppi.status ,su1.username operator from pro_price_inqury ppi left join pro_cus_user pcu on ppi.buyer_company_id = pcu.user_id left join system_users su on ppi.buyer_company_id = su.id left join system_users su1 on ppi.operator = su1.id
and pcu.company_name like concat('%',#{param.buyerCompanyName},'%')
and ppi.status = #{param.status}
