mapper.select:
/*供货商信息分页查询*/ select psi.id,psi.name,ppt.type_name_cn,ppt.type_name_en,psi.contact,psi.phone,psi.reason_price, psi.reason_quality,psi.reason_service,psi.product,psi.evaluation from pro_supply_info psi left join pro_product_type ppt on psi.type_id = ppt.type_id
and ppt.type_id = #{param.getTypeId}
and psi.name like concat('%',#{param.name},'%')
